import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

import nat1 from '../../images/nat-1-large.jpg';
import nat2 from '../../images/nat-2-large.jpg';
import nat3 from '../../images/nat-3-large.jpg';

const Section = styled.section`
    background-color: ${({ theme }) => theme.lightGrey1};
    padding: 35vh 0;
    margin-top: -20vh;
    text-align: center;

    h2 {
        display: inline-block;
        font-size: 2rem;
        text-transform: uppercase;
        font-weight: 700;
        background-image: linear-gradient(
            to right,
            ${({ theme }) => theme.primaryColorLight},
            ${({ theme }) => theme.primaryColorDark}
        );
        letter-spacing: 0.2rem;
        transition: all 1s;
        background-clip: text;
        -webkit-background-clip: text;
        color: transparent;
        margin-bottom: 4.5rem;

        &:hover {
            transform: skewY(1deg) skewX(7deg) scale(0.9);
            text-shadow: 0.5rem 1rem 2rem ${({ theme }) => theme.shadow};
        }
    }
`;

const Container = styled.div`
    display: grid;
    grid-template-rows: 60vh;
    grid-template-columns: 1fr 1fr;
    grid-gap: 2rem;

    div {
        padding: 0 3rem;
    }

    .info {
        align-items: center;
        grid-row: 1/2;
        grid-column: 1/2;
        text-align: left;
        font-size: ${({ theme }) => theme.dfltFontSize};

        h3 {
            text-transform: uppercase;
            font-weight: 700;
            margin-bottom: 0.4rem;
        }

        p {
            font-weight: 400;

            &:not(:last-child) {
                margin-bottom: 3rem;
            }
        }
    }

    .pics {
        grid-row: 1/2;
        grid-column: 2/3;
        position: relative;

        img {
            width: 40%;
            box-shadow: 0 1.5rem 4rem ${({ theme }) => theme.shadow};
            border-radius: 2px;
            position: absolute;
        }

        .pic1 {
            top: -2%;
            left: 0%;
        }

        .pic2 {
            top: 5%;
            left: 50%;
        }

        .pic3 {
            bottom: 20%;
            right: 15%;
        }
    }
`;

const NvgLink = styled(Link)`
    color: ${({ theme }) => theme.primaryColor};
    display: inline-block;
    text-decoration: none;
    padding: 5px;
    border-bottom: 1px solid ${({ theme }) => theme.primaryColor};
    font-size: ${({ theme }) => theme.dfltFontSize};
    transition: all 0.3s;

    &:hover {
        transform: translateY(-3px);
        box-shadow: 0 10px 20px ${({ theme }) => theme.shadow};
        color: ${({ theme }) => theme.white};
        background-color: ${({ theme }) => theme.primaryColor};
        border-radius: 0.5rem;
    }

    &:active {
        transform: translateY(-1px);
        box-shadow: 0 5px 10px ${({ theme }) => theme.shadow};
    }
`;

const About = () => {
    return (
        <Section>
            <h2>Exciting tours for adventurous people</h2>
            <Container>
                {/* TODO remove placeholders */}
                <div className='info'>
                    <h3>You're going to fall in love with nature</h3>
                    <p>
                        Lorem ipsum dolor, sit amet consectetur adipisicing
                        elit. Reprehenderit tenetur libero iste in dignissimos
                        inventore ducimus sunt tempore, nam aut deserunt esse
                        mollitia impedit quis, natus perspiciatis debitis. Quae,
                        hic!
                    </p>
                    <h3>Live adventurous like never before</h3>
                    <p>
                        Lorem ipsum dolor, sit amet consectetur adipisicing
                        elit. Reprehenderit tenetur libero iste in dignissimos
                        inventore ducimus sunt tempore, nam aut deserunt esse
                    </p>
                    {/* TODO add a link */}
                    <NvgLink to='/'>Learn More &rarr;</NvgLink>
                </div>
                <div className='pics'>
                    <img className='pic1' src={nat1} alt='pic1' />
                    <img className='pic2' src={nat2} alt='pic2' />
                    <img className='pic3' src={nat3} alt='pic3' />
                </div>
            </Container>
        </Section>
    );
};

export default About;
