import React from 'react';
import styled from 'styled-components';
import { FiGlobe } from 'react-icons/fi';

import FeatureBox from '../../components/Feature-box';
import backpic from '../../images/nat-4.jpg';
import { green_background_photo } from '../../style/Mixins';

const Section = styled.section`
    display: grid;
    padding: 0 10rem;
    grid-template-rows: 105vh;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 5rem;
    align-items: center;
    margin-top: -20vh;
    ${green_background_photo(backpic)}
    clip-path: polygon(0 20vh, 100% 0, 100% 85vh, 0 100%);
    text-align: center;
    font-size: 1.2rem;

    svg {

        background-image: linear-gradient(
            to right,
            ${({ theme }) => theme.primaryColorLight},
            ${({ theme }) => theme.primaryColorDark}
        );
        background-clip: text;
    -webkit-background-clip: text;
    }
`;

const Icon = styled(FiGlobe)`
    margin-bottom: 1rem;
    font-size: 3rem;

    color: transparent;
`;

export default function Features() {
    return (
