import { green_background_photo } from '../../style/Mixins';

const Section = styled.section`
    display: grid;
    padding: 0 10rem;
    grid-template-rows: 105vh;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 5rem;
    align-items: center;
    margin-top: -20vh;
    ${green_background_photo(backpic)}
    clip-path: polygon(0 20vh, 100% 0, 100% 85vh, 0 100%);
    text-align: center;
`;

const Icon = styled.div`

svg{
    color:green;
}
`

